mapper.insert:

	SELECT COALESCE(MAX(CAST(BOARD_NO AS INTEGER)),0)+1 AS BOARD_NO FROM BOARD_TB

INSERT INTO BOARD_TB
(
	BOARD_NO
	,CTGR_CD
	,BOARD_TITLE
	,BOARD_CONTENT
	,INPUT_NO
	,INPUT_DT
) VALUES (
	#{boardNo}
	,#{ctgrCd}
	,#{boardTitle}
	,#{boardContent}
	,#{inputNo}
	,now()
)

mapper.update:
UPDATE BOARD_TB
SET
	CTGR_CD = #{ctgrCd}
	,BOARD_TITLE = #{boardTitle}
	,BOARD_CONTENT = #{boardContent}
WHERE BOARD_NO = #{boardNo}
