mapper.select:
SELECT OUT_CD
FROM BLACKLIST_TB 
WHERE PER_NO=#{no}

mapper.insert:

	SELECT NEXTVAL('BUS_SEQ') 

INSERT INTO BUSINESS_TB VALUES
(
	'B'||#{bus_seq}
	,#{bus_email, jdbcType=VARCHAR}
	,#{bus_pw, jdbcType=VARCHAR}
	,#{bus_name, jdbcType=VARCHAR}
	,#{bus_ceo, jdbcType=VARCHAR}
	,#{bus_lcno, jdbcType=VARCHAR}
	,#{bus_lcno_file_id, jdbcType=VARCHAR}
	,#{bus_phone, jdbcType=VARCHAR}
	,#{bus_post, jdbcType=VARCHAR}
	,#{bus_addr, jdbcType=VARCHAR}
	,#{bus_dtl_addr, jdbcType=VARCHAR}
	,#{bus_file_id, jdbcType=VARCHAR}
	,'O'
	,default
	,NOW()
	,default
	,NOW()
)

mapper.select:
SELECT EMAIL, #{type} as type
FROM LOGIN_VIEW 
WHERE NAME=#{per_name} 
	AND	PHONE=#{per_phone} 
	AND NO LIKE '${type}%'


SELECT RND_NMBR 
FROM RANDOM_TB 
WHERE RND_EMAIL=#{email}

SELECT RND_EMAIL
	, RND_NMBR
FROM RANDOM_TB 
WHERE RND_EMAIL=#{rndEmail}
	AND RND_NMBR=#{rndCode}
	AND RND_DT BETWEEN (CURRENT_TIMESTAMP - INTERVAL '30 MIN') AND CURRENT_TIMESTAMP

mapper.update:
UPDATE PERSONAL_TB 
SET PER_PW=#{newPw} 
WHERE PER_EMAIL=#{email}

UPDATE BUSINESS_TB 
SET BUS_PW=#{newPw} 
WHERE BUS_EMAIL=#{email}
